};   

namespace taskF {     
    vector<int>tmp; 
    int mark[N],arr[N]; 
    int hd[N],to[N<<1],nex[N<<1],edges,cnt;    
    void add(int u,int v) {  
        nex[++edges]=hd[u]; 
        hd[u]=edges,to[edges]=v; 
    }  
    int Query(int x) { 
        int re=query(x),y; 
        for(int i=hd[x];i!=-1;i=nex[i]) { 
            y=to[i]; 
            re^=mark[y];  
        }  
        return re;  
    }  
    void Report(int x,int y) {   
        ++cnt;  
        add(y,x),add(x,y),report(x,y);   
    }
    void work(int l,int r,vector<int>&a) {   
        if(l==r) {  
            for(int i=0;i<a.size();++i) {   
                if(a[i]!=l) Report(arr[l],arr[a[i]]);  
            }  
            return;  
        }         
        vector<int>ql,qr;  
        int mid=(l+r)>>1;   
        for(int i=l;i<=mid;++i) {      
            modify(arr[i]),mark[arr[i]]=1;  
        }      
        for(int i=0;i<a.size();++i) {       
            int v=a[i];  
            if(v<=mid||Query(arr[v])) { 
                ql.pb(v); 
            }      
            else {           
                qr.pb(v); 
            }
        } 
        for(int i=l;i<=mid;++i) { 
            modify(arr[i]),mark[arr[i]]=0; 
        }   
        work(l,mid,ql),work(mid+1,r,qr);  
    }
    void solve(int n,int m) {  
        memset(hd,-1,sizeof(hd)); 
        for(int i=0;i<n;++i) { 
            arr[i]=i; 
        }       
        random_shuffle(arr,arr+n);  
        while(cnt<m) {         
            vector<int>tmp; 
            for(int i=0;i<n;++i) tmp.pb(i);                 
            work(0,n-1,tmp);      
            if(cnt<m) {  
                for(int i=0;i<n;++i) {      
                    if(check(arr[i])) {   
                        swap(arr[i],arr[--n]);        
                        --i;  
                    }
                }    
                random_shuffle(arr,arr+n);  
            }             
        }
    }
}; 
