inline int read()
{
    int x=0,f=1;
    char ch=getchar();
    while(ch<'0'||ch>'9'){
        if(ch=='-')
            f=-1;
        ch=getchar();
    }
    while(ch>='0'&&ch<='9'){
        x=(x<<1)+(x<<3)+(ch^48);
        ch=getchar();
    }
    return x*f;
}//快读可有可无
int lowbit(int x)
{
	return x&(-x);
}
void update1(int x,int y)
{
	while(x<=2*maxn)
	{
		c[x]+=y;
		x+=lowbit(x);
	}
}
int sum1(int x)
{
	 xans=0;
	while(x)
	{
		xans+=c[x];
		x-=lowbit(x);
	}
	return xans;
}
void update2(int x,int y)
{
	while(x<=2*maxn)
	{
		C[x]+=y;
		x+=lowbit(x);
	}
}
int sum2(int x)
{
	 xans=0;
	while(x)
	{
		xans+=C[x];
		x-=lowbit(x);
	}
	return xans;
}//树状数组的板子，只不过我这里写了两遍，可以加上一个数组的参数，这样就只需要写一遍了
