{
	string s;
	int i,x,k;
	n=read();
	for(i=1;i<=n;i++)
	{
		cin>>s;
		if(s=="Add")
		{
			a=read();b=read();c1=read();
			num=(c1-b)/a;//a，b，c要用double型
			if(a>0){
				if(num>1e6){
					ts[++tail]=1;
              continue;//恒不成立的情况，标记一下就可以了
				}
				else if(num<-1e6){
					st[++tail]=1;
					res++;continue;//恒成立的情况，计数++
				}
			}
			else if(a<0){
				if(num>1e6){
					st[++tail]=1;
					res++;continue;
				}
				else if(num<-1e6){
					ts[++tail]=1;
              continue;
				}	
			}
			if(a>0){
				d=(int)floor(num)+1;
				d+=maxn;
				q[++tail].a=a;
				q[tail].d=d;
				update1(d,1);
			}
			else if(a<0){
				d=(int)ceil(num)-1;
				d=maxn-d;//区间倒置
				q[++tail].a=a;
				q[tail].d=d;
				update2(d,1);//树状数组统计
			}
			else if(a==0){
				if(c1-b<0) {
					res++;
					st[++tail]=1;
				}
				else if(c1-b>=0){
					ts[++tail]=1;
				}
			}
		}
		else if(s=="Del")
		{
			x=read();
			if(vis[x]==1) continue;
			if(ts[x]==1){
				ts[x]=0;
			}
			else if(st[x]==1){
				res--;
				st[x]=0;
			}
			else if(q[x].a>0){
				update1(q[x].d,-1);
			}
			else if(q[x].a<0){
				update2(q[x].d,-1);
			}
			vis[x]=1;
		}
		else if(s=="Query")
		{
			k=read();
			cnt=sum1(k+maxn);
			cet=sum2(maxn-k);//查询的时候自然也要转置
			ans=cnt+cet+res;
			sna[++head]=ans;
		}
	}
	for(i=1;i<=head;i++)
