}
void modify2(int x,int k)
{
	for(int i=x;i<(MR<<1);i+=lowbit(i))
		bitree2[i]+=k;
}
int query2(int x)
{
	int ans=0;
	for(int i=x;i;i-=lowbit(i))
		ans+=bitree2[i];
	return ans;
}
int main()
{
//	freopen("P5482.in","r",stdin);
//	freopen("P5482.out","w",stdout);
