			else if(op[x]==2)
				modify2(1e6-res[x]+1,-1);
			else if(op[x]==3)
				--cnt;
			book[x]=1;
		}
		else
		{
			int x=read();
			printf("%d\n",cnt+query1(x+1e6+1)+query2(1e6-x+1));
		}
	}
	return 0;
}

```

