	if(Real_quick(n,(p-1)/2,p)==p-1) return -1;
	int a;
	while(1)
	{
		a=rand()%p;
		w=((a*a%p-n)%p+p)%p;
		if(Real_quick(w,(p-1)/2,p)==p-1)
			break;	
	}	
	Complex ret;
	ret.x=a;
	ret.y=1;
	return Image_quick(ret,(p+1)/2);
}
signed main()
{
	T=read();
	while(T--)
	{
		n=read();
		p=read();
		if(n==0)
		{
			printf("0\n");
			continue; 
		}
		int flag=cipolla(n,p);
		if(flag==-1)
		{
			printf("Hola!\n");
			continue;	
	 	}
	 	else
	 	{
	 		int sum=p-flag;
			if(sum==flag) printf("%lld\n",flag);
			else printf("%lld %lld\n",min(sum,flag),max(sum,flag));	
		}
	}
	return 0;
}
