	  sinv[i]=inv[i]+sinv[i-1]; //逆元前缀和
      for(register int i=0;i<=n;i++)
      {
          sss[i]=(sss[i-1]+ss[i]*inv[i])%mod;//整体前缀和
      }
	for(register int i=1;i<=q;i++)
	{

		int l,r;
