		if(!judge_m(c,d,f[a],h[a]))continue;
		if(len(minusv(c,d))<minl&&!eql(c,d)&&hp[i]>0)minl=len(minusv(c,d)),g=i;
	}
	return g;
}//求出子弹能攻击到的目标
