				case 'D':c_down(j);break;
				case 'L':c_left(j);break;
				case 'R':c_right(j);break;
				case 'F':c_fix(j);break;
				case 'M':c_mat(j);break;
				case 'A':c_atk(j);break;
			}
		}
	}
	for(rint i=1;i<=n;++i)
		printf("%d %d %d %d\n",pla[i].x,pla[i].y,pla[i].z,pla[i].hp);
	return 0;
}
