				else p[k].f--;
				break;
			}
			case 'F': {
				p[k].hp+=p[k].fix;
				break;
			}
			case 'A': {//子弹
				int x=p[k].x,y=p[k].y,z=p[k].z;//记录子弹位置
				bool f=0;//是否打中过飞机
				for(register int i=1;i<=2000;i++){
					x+=fx[p[k].h][p[k].f];
					y+=fy[p[k].h][p[k].f];
