				}
				break;
			}
			case 'M':{//和子弹几乎一样，不同之处在于打中了依然继续
				int x=p[k].x,y=p[k].y,z=p[k].z;
				for(register int i=1;i<=2000;i++){
					x+=fx[p[k].h][p[k].f];
					y+=fy[p[k].h][p[k].f];
					z+=fz[p[k].h][p[k].f];
					for(register int j=1;j<=n;j++){
						if(p[j].x==x&&p[j].y==y&&p[j].z==z&&p[j].alive){
							p[j].hp-=max(0,p[k].mat-p[j].mdf);
							if(p[j].hp<=0)p[j].alive=0;
						}
					}
				}
