int tot=0;//累计第一行和第一列之和 
int minn;//当前所有情况中第一行和第一列之和最小值 
int n;
bool vis[110];
int a[15][15];
int Q[15][15];//盛放最终答案 
vector<int> s[101][101]; 
bool tag;//标记是否有解 
void dfs(int x,int y){
	if(tot>=minn)
		return;
	if(x==n+1){
		if(tot>=minn)
			return;
		minn=tot;
		for(int i=1;i<=n;i++)
			for(int j=1;j<=n;j++)
				Q[i][j]=a[i][j];
		tag=1;
		if(n%2==1){
			if(tot==n*(2*n-1)){
				for(int i=1;i<=n;i++){
					for(int j=1;j<=n;j++)
						cout<<Q[i][j]<<' ';
					cout<<endl;
				}
				exit(0);
			}
		}
		if(n%2==0){
			if(tot==n*(2*n-1)+1){
				for(int i=1;i<=n;i++){
					for(int j=1;j<=n;j++)
						cout<<Q[i][j]<<' ';
					cout<<endl;
				}
				exit(0);
			}
		}
	}
	if(x==1){
		for(int i=0;i<s[0][a[x][y-1]].size();i++){
			if(!vis[s[0][a[x][y-1]][i]]){
				vis[s[0][a[x][y-1]][i]]=1;
				a[x][y]=s[0][a[x][y-1]][i];
				tot+=s[0][a[x][y-1]][i];
				if(y==n)
					dfs(x+1,1);
				else
					dfs(x,y+1);
				vis[s[0][a[x][y-1]][i]]=0;
				tot-=s[0][a[x][y-1]][i];
			}
		}
	}
	if(y==1){
		for(int i=0;i<s[0][a[x-1][y]].size();i++){
			if(!vis[s[0][a[x-1][y]][i]]){
				vis[s[0][a[x-1][y]][i]]=1;
				a[x][y]=s[0][a[x-1][y]][i];
				tot+=s[0][a[x-1][y]][i];
				if(x==n)
					dfs(2,2);
				else 
					dfs(x+1,1);
				vis[s[0][a[x-1][y]][i]]=0;
				tot-=s[0][a[x-1][y]][i];
			}
		}
	}
	if(x!=1&&y!=1){
		for(int i=0;i<s[a[x-1][y]][a[x][y-1]].size();i++){
			if(!vis[s[a[x-1][y]][a[x][y-1]][i]]){
				vis[s[a[x-1][y]][a[x][y-1]][i]]=1;
				a[x][y]=s[a[x-1][y]][a[x][y-1]][i];
				if(y==n)
					dfs(x+1,2);
				else
					dfs(x,y+1);
				vis[s[a[x-1][y]][a[x][y-1]][i]]=0;
			}
		}
	}
}
int main(){
	cin>>n;
	for(int i1=0;i1<=n*n;i1++){
		for(int j1=0;j1<=n*n;j1++){
			for(int k1=1;k1<=n*n;k1++){
				if(k1==i1||k1==j1||i1==j1)
					continue;
				if((p[k1+i1]||i1==0)&&(p[k1+j1]||j1==0))
					s[i1][j1].push_back(k1);
			}
		}
	}
	tot=0;
	minn=999999;
	a[1][1]=1;
	memset(vis,0,sizeof(vis));
	vis[1]=1;
	tag=0;
	dfs(1,2);
	if(!tag){
		cout<<"NO"<<endl;
		return 0;
	}
	for(int i=1;i<=n;i++){
		cout<<"	";
		for(int j=1;j<=n;j++)
			cout<<Q[i][j]<<' ';
		cout<<endl;
	}
	return 0;
}
