#pragma GCC optimize(2)
#pragma GCC optimize(3)
#pragma GCC optimize("-O2")
#pragma GCC optimize("-O3")
#pragma GCC optimize("inline")
#include<iostream>
#include<cstdio>
#include<algorithm>
#include<cstring>
#define pr pair<int,int>
#define mp make_pair
#define N 15
#define N1 105
#define N2 205
using namespace std;
int st,n,n0,n1,n2,lim,cnt,tot,kt,prime[N2],sh[N1],q[N1],a[N][N];
int pre[N1],nxt[N1];
int q1[N1][N1];
int q2[N1][N1][N1];
bool pri[N2],t[N1][N1];
pr s[N1];
bool vis[N1];
int dic[2][2]={{0,-1},{-1,0}};
int rans=1000000007,ans[N][N];
void dfs(int cs)
{
    if (cs==n1)
    {
        for (int i=1;i<=n;i++)
        {
            for (int j=1;j<=n;j++)
                printf("%d ",a[i][j]);
            putchar('\n');
        }
        exit(0);
        return;
    }
    int l=s[cs].first;
    int r=s[cs].second;
    if (cs<n0-1)
    {
        int k,o;
        if (l==1)
            k=a[l][r-1]; else
            k=a[l-1][r];
        if (n & 1)
        {
            for (int i=2;i<n0;i++)
                if (!vis[i] && t[i][k])
                {
                    vis[i]=true;
                    a[l][r]=i;
                    dfs(cs+1);
                    vis[i]=false;
                }
        } else
        {
            for (int i=2;i<=n0;i++)
                if (!vis[i] && t[i][k])
                {
                    if (i==n0-1)
                        continue;
                    vis[i]=true;
                    a[l][r]=i;
                    dfs(cs+1);
                    vis[i]=false;
                }
        }

    } else
    {
        int k1=a[l][r-1],k2=a[l-1][r];
        for (int i=1;i<=q2[k1][k2][0];i++)
            if (!vis[q2[k1][k2][i]])
            {
                vis[q2[k1][k2][i]]=true;
                a[l][r]=q2[k1][k2][i];
                dfs(cs+1);
                vis[q2[k1][k2][i]]=false;
            }
    }
}
void dfs2(int cs)
{
    if (cs<n+n)
        st+=a[s[cs-1].first][s[cs-1].second];
    if (st>=rans)
    {
        if (cs<n+n)
            st-=a[s[cs-1].first][s[cs-1].second];
        return;
    }
    if (cs==n1)
    {
        rans=st;
        for (int i=1;i<=n;i++)
            for (int j=1;j<=n;j++)
                ans[i][j]=a[i][j];
        return;
    }
    int l=s[cs].first;
    int r=s[cs].second;
    for (int i=nxt[0];i;i=nxt[i])
    {
        bool flag=true;
        for (int j=0;j<2;j++)
        {
            int nl=l+dic[j][0];
            int nr=r+dic[j][1];
            if (nl<1 || nl>n || nr<1 || nr>n || !a[nl][nr])
                continue;
            flag&=t[sh[i]][a[nl][nr]];
        }
        if (flag)
        {
            a[l][r]=sh[i];
            pre[nxt[i]]=pre[i];
            nxt[pre[i]]=nxt[i];
            dfs2(cs+1);
            a[l][r]=0;
            pre[nxt[i]]=i;
            nxt[pre[i]]=i;
        }
    }
    if (cs<n+n)
        st-=a[s[cs-1].first][s[cs-1].second];
}
int main()
{
    scanf("%d",&n);
    if (n==1)
    {
        puts("NO");
        return 0;
    }
    n0=n+n;
    n1=n*n;
    n2=n1 << 1;
    pri[1]=true;
    for (int i=2;i<=n2;i++)
    {
        if (!pri[i])
            prime[++cnt]=i;
        for (int j=1;j<=cnt;j++)
        {
            int g=i*prime[j];
            if (g>n2)
                break;
            pri[g]=true;
            if (i % prime[j]==0)
                break;
        }
    }
    for (int i=1;i<=n1;i++)
        for (int j=1;j<=n1;j++)
            if (i!=j && !pri[i+j])
                q[i]++,q1[i][q[i]]=j,t[i][j]=true;
    for (int i=1;i<=n1;i++)
        for (int j=1;j<=n1;j++)
            if (i!=j)
                for (int k=1;k<=n1;k++)
                    if (k!=i && k!=j)
                        if (!pri[i+k] && !pri[j+k])
                            q2[i][j][++q2[i][j][0]]=k;
    for (int i=2;i<=n;i++)
        s[++kt]=mp(1,i);
