	{
		double x = (double) (uint_rand() % 100000) / 100000;
		return x + 4;
	}
	inline double get_k()
	{
		double x = (double) (uint_rand() % 100000) / 100000;
		return (x + 1) * 5;
	}
	inline void read(double &n,double &a, double &b)
	{
		n = get_n(); a = get_k();
		if (op) b = a;
		else b = get_k(); 
	}
}
double solve(double num){
	double x=n;
	x-=(k1+k2-num)/(a*k3+b*k4);
	return x;
}
int main(){
	scanf("%d",&t);
	Mker::init();
	while(t--){
		Mker::read(n,a,b);
		k1=pow(n,a);
		k2=pow(n,b);
		k3=k1/n;
		k4=k2/n;
		int num=k1+k2;
