inline res cmp(res aa,res bb){
	res ret;
	if(aa.ans==0||bb.ans==0){
		ret.ans=0;
		return ret;
	}
	ret.ans=1;
	for(R int i=1;i<=n;i++){
		if(aa.x[i]==bb.x[i]){
