			ret.x[i]=bb.x[i];
		else ret.x[i]=aa.x[i];
	}
	return ret;
}
void build(tr *tree,int l,int r){
	if(l==r){
		tree->o.ans=1;
		for(R int i=1;i<=n;i++){
			tree->o.x[i]=a[l][i];
			if(a[l][i]==3) tree->o.ans*=2;
		}
		return;
