	int mid=(l+r)>>1;
	tree->ls=&dizhi[++tot];
	tree->rs=&dizhi[++tot];
	build(tree->ls,l,mid);
	build(tree->rs,mid+1,r);
	tree->o=cmp(tree->ls->o,tree->rs->o);
}
void change(tr *tree,int l,int r,int k){
	if(l==r){
