	if(k<=mid) change(tree->ls,l,mid,k);
	else change(tree->rs,mid+1,r,k);
	tree->o=cmp(tree->ls->o,tree->rs->o);
}
res qq(tr *tree,int l,int r,int ql,int qr){
	if(ql<=l&&r<=qr) {
