	int mid=(l+r)>>1;
	res aa,bb;
	aa.ans=bb.ans=-1;
	if(ql<=mid) aa=qq(tree->ls,l,mid,ql,qr);
	if(qr>mid) bb=qq(tree->rs,mid+1,r,ql,qr);
	if(aa.ans==-1) return bb;
	if(bb.ans==-1) return aa;
	return cmp(aa,bb);
}
