    n=read(),Q=read(),mod=read();
    for (re int i=1;i<=n;++i) a[i]=read();

    for (re int i=0;i<mod;++i) f[0][i]=i*i*i%mod;
    for (re int i=1;i<17;++i)
        for (re int j=0;j<mod;++j)
            f[i][j]=f[i-1][f[i-1][j]];

