#include<bits/stdc++.h>
#define LC x<<1
#define RC x<<1|1
using namespace std;
int tree[800080],fz[800080],fg[800080],ans[200020];//fz：反转标记，fg：覆盖标记，ans：最终答案 
char opt,c1,c2;
string s;
void pushup(int x){
	tree[x]=tree[LC]+tree[RC];
}
void pushdown(int x,int l,int r){//标记下传 
	int mid=(l+r)>>1;
	if(fg[x]!=-1){//先覆盖 
		tree[LC]=fg[x]*(mid-l+1);
		tree[RC]=fg[x]*(r-mid);
		fg[LC]=fg[x];
		fg[RC]=fg[x];
		//记得把反转标记变为 0 
		fz[LC]=0;
		fz[RC]=0; 
		fg[x]=-1;
	}
	if(fz[x]){//再反转 
		tree[LC]=mid-l+1-tree[LC];
		tree[RC]=r-mid-tree[RC];
		//注意：是异或自己 
		fz[LC]=!fz[LC];
		fz[RC]=!fz[RC];
		fz[x]=0;
	}
}
void mdffg(int x,int l,int r,int ql,int qr,int k){//覆盖操作 
	if(ql>qr) return;
	if(ql<=l&&r<=qr){
		tree[x]=k*(r-l+1);
		fg[x]=k;
		fz[x]=0;//记得反转标记赋为 0 
		return;
	}
	int mid=(l+r)>>1;
	pushdown(x,l,r);
	if(ql<=mid) mdffg(LC,l,mid,ql,qr,k);
	if(mid<qr) mdffg(RC,mid+1,r,ql,qr,k);
	pushup(x);
}
void mdffz(int x,int l,int r,int ql,int qr){//反转操作 
	if(ql>qr) return;
	if(ql<=l&&r<=qr){
		tree[x]=r-l+1-tree[x];
		fz[x]=!fz[x];
		return;
	}
	int mid=(l+r)>>1;
	pushdown(x,l,r);
	if(ql<=mid) mdffz(LC,l,mid,ql,qr);
	if(mid<qr) mdffz(RC,mid+1,r,ql,qr);
	pushup(x);
}
void query(int x,int l,int r){
	if(l==r){
		ans[l]=tree[x];//记录答案 
		return;
	}
	int mid=(l+r)>>1;
	pushdown(x,l,r);
	query(LC,l,mid);
	query(RC,mid+1,r);
}
void solve(){
	query(1,1,200000);
	if(!tree[1]){//如果所有数字的值都为 0，那么 S 就是空集 
		printf("empty set\n");
		return;
	}
	bool ck=0;
	//记得处理并集 
	for(int i=1;i<=200000;i++){
		if(!ck&&ans[i]){
			ck=1;
			if(i%2==0) printf("(");
			else printf("[");
			printf("%d,",(i-1)>>1);
		}
		if(ck&&!ans[i+1]){
			ck=0;
			printf("%d",i>>1);
			if(i%2==0) printf(") ");
			else printf("] ");
		}
	}
	printf("\n");
}
int main(){
	for(int i=0;i<=800079;i++) fg[i]=-1;//记得赋为-1 
	while(cin>>opt){
		int l,r;
		cin>>c1;
		scanf("%d,%d",&l,&r);
		cin>>c2;
		//处理括号，注意左开和右开不一样 
		if(c1=='(') l=2*l+2;
		else l=2*l+1;
		if(c2==')') r=2*r;
		else r=2*r+1;
		//5个操作，具体已在上面讲解
		//这里所有的200000即为值域 
		if(opt=='U') mdffg(1,1,200000,l,r,1);
		if(opt=='I'){
			mdffg(1,1,200000,1,l-1,0);
			mdffg(1,1,200000,r+1,200000,0);
		}
		if(opt=='D') mdffg(1,1,200000,l,r,0);
		if(opt=='C'){
			mdffz(1,1,200000,1,200000);
			mdffg(1,1,200000,1,l-1,0);
			mdffg(1,1,200000,r+1,200000,0);
		}
		if(opt=='S') mdffz(1,1,200000,l,r);
	}
	solve();//输出最终答案 
}
