```cpp
#include<cstdio>
#include<cstring>
#include<algorithm>
#include<set>
#define iter set<Node>::iterator
#define N 1000010
using namespace std;

struct Node {
	int l,r;bool f;
	inline bool operator < (const Node &o) const {
		return l<o.l;
	}
};

set<Node> odt;

iter split(int x) {
	if(x>1e9) return odt.end();
	iter it=--odt.upper_bound((Node){x,0,0});
	if(it->l==x) return it;
	int l=it->l,r=it->r;bool f=it->f;
	odt.erase(it);
	odt.insert((Node){l,x-1,f});
	return odt.insert((Node){x,r,f}).first;
}

void assign(int l,int r,bool f) {
	iter itr=split(r+1),itl=split(l);
	odt.erase(itl,itr);
	odt.insert((Node){l,r,f});
}

char ch;bool fl;

bool getch() {
	while(ch!=EOF && ch!='U' && ch!='I' && ch!='D' && ch!='C' && ch!='S') ch=getchar();
	if(ch==EOF) return false;
	else return true;
}

bool read1() {
	while(ch!='(' && ch!='[') ch=getchar();
	if(ch=='(') return false;
	else return true;
}

bool read2() {
	while(ch!=')' && ch!=']') ch=getchar();
	if(ch==')') return false;
	else return true;
}

template<class T>
void read(T &x) {
	x=0;ch=getchar();bool f=false;
	for(;ch!='-' && !(ch>='0' && ch<='9');ch=getchar());
	if(ch=='-') ch=getchar(),f=true;
	for(;ch>='0' && ch<='9';ch=getchar()) x=x*10+ch-48;
	if(f) x=-x;
}

int main() {
//	freopen("interval.in","r",stdin);
//	freopen("interval.out","w",stdout);
	odt.insert((Node){(int)-1e9,(int)1e9,0});
	int l,r;
	while(getch()) {
		if(ch=='U') {
			fl=read1();
			read(l);
			if(fl) l=l*2; else l=l*2+1;
			read(r);
			fl=read2();
			if(fl) r=r*2; else r=r*2-1;
			if(l>r) continue;
			assign(l,r,true);
			continue;
		}
		if(ch=='I') {
			fl=read1();
			read(l);
			if(fl) l=l*2; else l=l*2+1;
			read(r);
			fl=read2();
			if(fl) r=r*2; else r=r*2-1;
			if(l>r) continue;
			assign(-1e9,l-1,false);
			assign(r+1,1e9,false);
			continue;
		}
		if(ch=='D') {
			fl=read1();
			read(l);
			if(fl) l=l*2; else l=l*2+1;
			read(r);
			fl=read2();
			if(fl) r=r*2; else r=r*2-1;
			if(l>r) continue;
			assign(l,r,false);
			continue;
		}
		if(ch=='C') {
			fl=read1();
			read(l);
			if(fl) l=l*2; else l=l*2+1;
			read(r);
			fl=read2();
			if(fl) r=r*2; else r=r*2-1;
			if(l>r) continue;
			iter itr=split(r+1),itl=split(l);
			for(;itl!=itr;++itl) {
				bool f=!itl->f;
				int L=itl->l,R=itl->r;
				odt.erase(itl);
				itl=odt.insert((Node){L,R,f}).first;
			}
			assign(-1e9,l-1,false);
			assign(r+1,1e9,false);
			continue;
		}
		if(ch=='S') {
			fl=read1();
			read(l);
			if(fl) l=l*2; else l=l*2+1;
			read(r);
			fl=read2();
			if(fl) r=r*2; else r=r*2-1;
			if(l>r) continue;
			iter itr=split(r+1),itl=split(l);
			for(;itl!=itr;++itl) {
				bool f=!itl->f;
				int L=itl->l,R=itl->r;
				odt.erase(itl);
				itl=odt.insert((Node){L,R,f}).first;
			}
			continue;
		}
	}
	for(iter i=odt.begin();i!=odt.end();++i) {
		iter j=i;
		++j;
		if(j==odt.end()) break;
		if(i->r+1==j->l && i->f==j->f) {
			iter itr=split(j->r+1),itl=split(i->l);
			odt.erase(itl,itr);
			i=odt.insert((Node){i->l,j->r,i->f}).first;
			--i;
//			assign(i->l,j->r,i->f);
		}
	}
	bool flag=true;
	for(iter i=odt.begin();i!=odt.end();++i) {
		if(i->f) {
			flag=false;break;
		}
	}
	if(flag) puts("empty set");
	else {
		for(iter itl=odt.begin();itl!=odt.end();++itl) if(itl->f) {
			if(itl->l&1) {
				putchar('(');
				printf("%d",itl->l/2);
				putchar(',');
			} else {
				putchar('[');
				printf("%d",itl->l/2);
				putchar(',');
			}
			if(itl->r&1) {
				printf("%d",(itl->r+1)>>1);
				putchar(')');
				putchar(' ');
			} else {
				printf("%d",itl->r/2);
				putchar(']');
				putchar(' ');
			}
		}
		putchar('\n');
	}
	return 0;
}
