```cpp
#include<cstdio>
#include<cstring>
#include<algorithm>
#include<cmath> 
typedef long long ll;

using namespace std;
#define $(i,a,n) for(int i = a; i <= n; ++i)

inline ll read() {
	   ll ans = 0;
	   char last = ' ',ch = getchar();
	   while(ch < '0' || ch > '9') last = ch, ch = getchar();
	   while(ch >= '0' && ch <= '9') ans = (ans << 3) + (ans << 1) + ch - '0', ch = getchar();
	   if(last == '-') ans = -ans;
	   return ans;
}

const int INF = 0x3f3f3f3f;

const int M = 1e4 + 5;
const int N = 1e5 + 5;

char s[M][N];
char w[N], a[N], stack[N];
int cnt, top;

void func(char *c) {	// 该函数实现退格 为了好理解用栈实现
	top = 0;
	int len = strlen(c + 1);
	for(int i = 1; i <= len; ++i) {
		if(c[i] == '<') {
			if(top) {
				stack[top--] = '\0';
			}
		} else stack[++top] = c[i];
		c[i] = '\0'; 
	}
}
void clear() {
	while(top) stack[top--] = '\0';
}
ll ans;
bool flag = false;
int main(){
	while(true) {
		++cnt;
		gets(s[cnt] + 1);
		if(s[cnt][1] == 'E' && s[cnt][2] == 'O' && s[cnt][3] == 'F') {
			cnt--;
			break;
		}
		func(s[cnt]);

		for(int i = 1; i <= top; ++i) {
			s[cnt][i] = stack[i];
		}
		clear();
	}
	for(int i = 1; i <= cnt; ++i) {	// 只比较前cnt行
		gets(w + 1);
		if(w[1] == 'E' && w[2] == 'O' && w[3] == 'F') {
			flag = true;
			break;
		}
		func(w);
		int len2 = strlen(s[i] + 1);
		int len = min(top, len2);
		for(int j = 1; j <= len; ++j) {
			if(stack[j] == s[i][j]) ++ans;
		}
		clear();
	}
	while(!flag) {
		gets(w + 1);
		if(w[1] == 'E' && w[2] == 'O' && w[3] == 'F') {
			flag = true;
		}
	}
	int T = read();
	printf("%.0lf\n", round(60.0 * ans / T));
	return 0;
}

