$string$ 可以很好地解决这个问题，它自带了 $popback$ 与 $pushback$ 函数。

```cpp
#include <bits/stdc++.h>
using namespace std;

template <class T>
inline bool read(T &ret)
{
    char c;
    int sgn;
    if (c = getchar(), c == EOF)
    {
        return 0;
    }
    while (c != '-' && (c < '0' || c > '9'))
    {
        c = getchar();
    }
    sgn = (c == '-') ? -1 : 1;
    ret = (c == '-') ? 0 : (c - '0');
    while (c = getchar(), c >= '0' && c <= '9')
    {
        ret = ret * 10 + (c - '0');
    }
    ret *= sgn;
    return 1;
}

template <class T>
inline void write(T x)
{
    if (x > 9)
    {
        write(x / 10);
    }
    putchar(x % 10 + '0');
}

const int MAXN=100010;

string s1[MAXN],s2[MAXN];
string s[MAXN];
int t,cnt1=0,cnt2=0,tot=0;
string sr;

int main()
{
    while(getline(cin,sr))
    {
        if(sr=="EOF")
            break;
        s[++cnt1]=sr;
    }
    for(int i=1;i<=cnt1;i++)
    {
        sr.clear();
        int ccsz=s[i].size();
        for(int j=0;j<ccsz;j++)
        {
            if(s[i][j]=='<')
            {
                if(!sr.empty())
                    sr.pop_back();
            }
            else
                sr.push_back(s[i][j]);
        }
        s1[i]=sr;
    }
    while(getline(cin,sr))
    {
        if(sr=="EOF")
            break;
        s[++cnt2]=sr;
    }
    for(int i=1;i<=cnt2;i++)
    {
        sr.clear();
        int ccsz=s[i].size();
        for(int j=0;j<ccsz;j++)
        {
            if(s[i][j]=='<')
            {
                if(!sr.empty())
                    sr.pop_back();
            }
            else
                sr.push_back(s[i][j]);
        }
        s2[i]=sr;
    }
    for(int i=1;i<=min(cnt1,cnt2);i++)
    {
        int sz1=s1[i].size(),sz2=s2[i].size();
        for(int j=0;j<min(sz1,sz2);j++)
        {
            if(s1[i][j]==s2[i][j])
                tot++;
        }
    }
    cin>>t;
    tot=(int)((double)((double)tot/(double)t)*(double)60);
    cout<<tot<<endl;
