## code：

```cpp
#include<bits/stdc++.h>
using namespace std;

const int N = 1e5 + 10;
double T;
int len1 = 0, len2 = 0;
string str1[N], str2[N];
double sum = 0;

int main() {
	while(1) {
		string s;
		getline(cin, s);
		if(s[0] != 'E')	str1[++len1] = s;
		else	break;
	}
	while(1) {
		string s;
		getline(cin, s);
		if(s[0] != 'E')	str2[++len2] = s;
		else	break;
	}
	cin >> T;
	for(int i = 1; i <= len1; ++i) {
		if(i > len2)	break;
		char str3[N], str4[N];
		int x = str1[i].size(), y = str2[i].size(), top = 0, top1 = 0;
		for(int j = 0; j < x; ++j) {
			if(str1[i][j] == '<' && !top1)	continue;
			if(str1[i][j] == '<' && top1) {top1--; continue; }
			str4[top1++] = str1[i][j];
		}
		for(int j = 0; j < y; ++j) {
			if(str2[i][j] == '<' && !top)	continue;
			if(str2[i][j] == '<' && top) {top--; continue; }
			str3[top++] = str2[i][j];
		}
		for(int j = 0; j < min(top, top1); ++j)	if(str3[j] == str4[j])	sum++;
	}
	printf("%.0lf", (1.0 * sum * 60) / (1.0 * T));
	return 0;
}

```

