		{
			while(s[0]=='<')//删除行头的'<'
				s.erase(0,1);
			if(s[i]=='<')//当遇到'<'是删掉'<'及前面的一个字符
			{
				s.erase(i-1,2);
				i=0;
			}
		}
		fw[pos]=s;
		pos++;
	}
	pos=1;
	while(true)
	{
		string s;
		getline(cin,s);
		if(s=="EOF")
			break;
		for(int i=0;i<s.size();i++)
		{
			while(s[0]=='<')
				s.erase(0,1);
			if(s[i]=='<')
			{
				s.erase(i-1,2);
				i=0;
			}
		}//同上处理'<'
		for(int i=0;i<min(fw[pos].size(),s.size());i++)//与范文对应的行比较
			if(s[i]==fw[pos][i])//逐位比较
				correct++;
		pos++;
	}
	int t;
	cin>>t;
	cout<<(int)((correct*60)/(t*1.0)+0.5)<<endl;//四舍五入
