void add(int x, int y){
    cnt++;
    Next[cnt] = head[x];
    head[x] = cnt;
    vet[cnt] = y;
}

int s[MAXM], lc[MAXM], rc[MAXM], rt[MAX];
void build(int &p, int l, int r){
    if(!p) p = ++tot;
    if(l == r) return;
    build(lc[p], l, mid);
    build(rc[p], mid+1, r);
}

void update(int &p, int l, int r, int x, int last){
    p = ++tot;
    lc[p] = lc[last], rc[p] = rc[last], s[p] = s[last]+1;
    if(l == r) return;
    if(mid >= x) update(lc[p], l, mid, x, lc[last]);
    else update(rc[p], mid+1, r, x, rc[last]);
}

int query(int l, int r, int p, int x, int y, int z){      //z:lca
    if(l == r) return s[x]+s[y]-2*s[z];
    if(mid >= p) return query(l, mid, p, lc[x], lc[y], lc[z]);
    else return query(mid+1, r, p, rc[x], rc[y], rc[z]);
}

void dfs(int x, int fa){
    dep[x] = dep[fa]+1, sz[x] = 1;
    f[x][0] = fa;
    for(int i = 1; i <= 20; i++) f[x][i] = f[f[x][i-1]][i-1];
    update(rt[x], 1, n, col[x], rt[fa]);
    for(int i = head[x]; i; i = Next[i]){
        int v = vet[i];
        if(v == fa) continue;
        dfs(v, x);
        sz[x] += sz[v];
    }
}

int Lca(int x, int y){
    if(dep[x] < dep[y]) swap(x, y);
    for(int i = 20; i >= 0; i--){
        if(dep[f[x][i]] >= dep[y]) x = f[x][i];
    }
    if(x == y) return x;
    for(int i = 20; i >= 0; i--){
        if(f[x][i] != f[y][i]) x = f[x][i], y = f[y][i];
    }
    return f[x][0];
}
int get(int x, int fa){
    for(int i = 20; i >= 0; i--){
        if(dep[f[x][i]] > dep[fa]) x = f[x][i];
    }
    return x;
}

bool cmp(int a, int b){
    return dep[a] > dep[b];
}

ll pre[MAX];

int main()
{
    cin >> n;
    for(int i = 1; i <= n; i++){
        read(col[i]);
        v[col[i]].push_back(i);
    }
    int x, y, lca = 0;
    for(int i = 1; i < n; i++){
        read(x), read(y);
        add(x, y), add(y, x);
    }
    build(rt[0], 1, n);
    dfs(1, 0);
    for(int i = 1; i <= n; i++){
        for(int j = head[i]; j; j = Next[j]){
            int v = vet[j];
            if(v == f[i][0]) continue;
            pre[i] += (ll)sz[v]*(n-sz[v]-1);
        }
        pre[i] += (ll)(n-sz[i])*(sz[i]-1);
        pre[i] /= 2;
        pre[i] += n-1;
    }
    for(int i = 1; i <= n; i++){
        if(v[i].empty()){
            write((ll)n*(n-1)/2), puts("");
            continue;
        }
        sort(v[i].begin(), v[i].end(), cmp);
        x = v[i][0];
        if(v[i].size() == 1){
            write(pre[x]), puts("");
            continue;
        }
        y = v[i][v[i].size()-1];
        for(int j = 1; j < v[i].size(); j++){
            lca = Lca(x, v[i][j]);
            if(lca != v[i][j]){
                y = v[i][j];
                break;
            }
        }
        if(y != lca){
            int t = query(1, n, i, rt[x], rt[y], rt[lca]);
            if(col[lca] == i) t++;
            if(t == v[i].size()){
                write((ll)sz[x]*sz[y]), puts("");
            }
            else puts("0");
        }
        else{
            write((ll)sz[x]*(n-sz[get(x, lca)])), puts("");
        }
    }

    return 0;
}
```

