			}
			continue;
		}
		int la1=tail[w(i)][0],la2=tail[w(i)][1];
		bool ok=0;
		if(dfn[la1]+siz[la1]>dfn[b(i)] && dfn[b(i)]>dfn[la1])
			tail[w(i)][0]=b(i),ok=1;
		if(dfn[la2]+siz[la2]>dfn[b(i)] && dfn[b(i)]>dfn[la2])
			tail[w(i)][1]=b(i),ok=1;
		if(!ok)
			p[w(i)]=1;
	}
}//构建初始链
void solve()
{
	for(int i=1;i<=n;i++)
	{
