int qp(int a, int k){
	int ret = 1; double _a = a;
	Res = 1.0;
	while(k){
		if(k & 1) ret = ret * a, Res = Res * _a;
		a = a * a;
		_a = _a * _a;
		k >>= 1;
	}
	return ret;
}

