int n,m,k;
int a[maxn][maxn],ans[maxn];
bool b[maxn][maxn];//忽略
int main(){
	n=read(); m=read(); k=read();
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			int x=read();
			a[i][x]=j;//第i个人第x天做的第j套试题
		}	
	}
	for(int i=1;i<=n;i++){
		for(int j=1;j<=k;j++){
