	scanf("%d%d%d",&n,&m,&k);
	for(int i=1;i<=n;i++)
		for(int j=1;j<=m;j++)
			scanf("%d",&a[i][j]);
	for(int i=1;i<=m;i++){
		memset(used,0,sizeof used);
		for(int j=1;j<=n;j++){
			if(!used[a[j][i]]){
				used[a[j][i]]=1;
				ans[a[j][i]]++;
			}
		}
	}
	for(int i=1;i<=k;i++)
		cout<<ans[i]<<" ";
	return 0; 
}
