int main()
{
    while(cin>>a[n])n++;//输入序列
    int m=n-1;
    n--;//一定减一
    while(n)//从后往前
    {
        if(a[n]=='X')//小X大
        {
            a1[n]='2';
            a2[n]='1';//其实这两个数随便，符合要求就好
            flag=0;//已经不等
        }
        if(a[n]=='Y')小Y大
        {
            a1[n]='1';
            a2[n]='2';//同上，随意
            flag=0;已经不等
        }
        if(a[n]=='Z')
        {
