	for (int i = lens - 1; i >= 0; i--) {
		if (s[i] == 'X') {//是x就小x大小y小
			xx[i] = '2';
			xy[i] = '1';
		} else if (s[i] == 'Y') {//是y那就是小x小，小y大
			xx[i] = '1';
			xy[i] = '2';
		} else xx[i] = xy[i] = '0';//相等侧为零
	}
	for (int i = 0; i < lens; i++) putchar(xx[i]);
	puts("");
