using namespace std;

const int maxn = 1234567;
char s[maxn], ax[maxn], ay[maxn];

bool check(int len) {
    int p = len - 1;
    while(~p && s[p] == 'Z') --p;
    for(int i = 0; i <= p; ++i)
        if(s[i] == 'Z')
            return true;
    return false;
}

int main() {
