    	if(x[i] == 'Z' && x[i + 1] != x[i])
        	return end();
	for(i = 0;i < s;++i)
    	cout << (x[i] == 'X' ? 1 : 0);//是X就说明此时X大，用1表示
	cout << endl;
	for(i = 0;i < s;++i)
    	cout << (x[i] == 'Y' ?	1 : 0);
//Z的话两个都是0，就是相等的
	return 0;
