#include<bits/stdc++.h>
using namespace std;
typedef long long ll;

ll a,b;
ll ans;

int main(){
	cin>>a>>b;
	if(a==b&&a==0){
		puts("inf");
		return 0;
	}
	if(a==1&&b==1){
		puts("1");
		return 0;
	}
	if(a==1&&b==2){
		puts("1");
		return 0;
	}
	if(a==2&&b==2){
		puts("0");
		return 0;
	}
	if(a*a==4*b){
		puts("inf");
		return 0;
	}
	if(a==0){
		ans=0;
		ll temp=sqrt(b);
		for(ll i=1;i<=temp;i++)
			if(b%i==0 && (i+b/i)%2==0) {
				ans++;
			}
		cout<<ans;
		return 0;
	}

	ll tp=sqrt(b);
	for(ll i=0;i<=max(a/2,tp);i++){
		ll temp1=b-i*i;
		ll temp2=2*i-a;
		if(temp1==temp2&&temp1==0){
			puts("inf");
			return 0;
		}
		if(temp2==0) continue;
		if(temp1/temp2<0) continue;
		if(temp1%temp2!=0) continue;
		ans++;
	} 
	cout<<ans;
	return 0;
}
