（写得不是特别好，毕竟比赛时候写的考虑不周全）

```cpp
#include<iostream>
#include<cmath>
using namespace std;
long long a,b,ans,l,r;
int main()
{
    cin>>a>>b;
    if (a%2==0 && b==(a/2)*(a/2))
    {
        cout<<"inf";
        return 0;
    }
    if (a/2<floor(sqrt(b)))
    {
        l=a/2;
        r=floor(sqrt(b));
    }
    else
    {
        r=a/2;
        l=floor(sqrt(b));
    }
    for (long long m=r; m>=l; m--)
    {
        if (a==2*m)
            continue;
        if ((m*m-b)/(a-2*m)<0)
            break;
        if((double)(m*m-b)/(a-2*m)==(m*m-b)/(a-2*m))
            ans++;
    }
    cout<<ans;
    return 0;
}
