int n,a[300001];
priority_queue<int> q;
int main() {
	scanf("%d",&n);
	for(int i=1;i<=n;++i)
	{
		scanf("%d",&a[i]);
		q.push(a[i]);//放进堆里
	}

	long long baofu=0,manzu=0;

	double shufu;

	double mx=-1;

	for(int i=1;i<=n;++i)
	{
		baofu++;//饱腹感
		manzu+=q.top();//满足感
		q.pop();//移除出堆
		mx=max(mx,1.0*manzu*manzu/baofu);//最大舒适感
