#include <set>
#define MAXIOLG 25
#define MAXN 500005
#define MAXM 1000005
#define INf 114514
#define INF 1919810

using namespace std;

typedef long long ll;
typedef long double ld;
typedef ll io_t;

io_t shin[MAXIOLG];
io_t seto(void);
void ayano(io_t x,char spliter='\n');

priority_queue<int,vector<int>,greater<int> > q; //用于贪心的小根堆
set<pair<int,int> > st; //pair 自带比较函数

int fst[MAXN],nxt[MAXM],edges=0;
int g[MAXM];
int ind[MAXN];
int pre[MAXN];
int f[MAXN];

void addedge(int u,int v);

int main(void){
    int n,m;
    n=seto(),m=seto();
    while (m--){
        int u,v;
        u=seto(),v=seto();
        addedge(u,v),ind[v]++;
    }
    for (int i=1;i<=n;i++)
        (!ind[i])?(q.push(i),pre[i]=0):(0),
        f[i]=INF;
    int ans1=0;
    int tx=0;
    f[0]=0;
    st.insert(make_pair(0,0));
    while (!q.empty()){
        int nowv=q.top();
        q.pop();
        int tpre=pre[nowv];
        //以下是 dp
        if (nowv>tpre){
            //计算 f 值; first 保存"值"——标号,second 保存"下标"——代价
            f[nowv]=st.lower_bound(make_pair(tpre,0))->second+1;
            int tf=f[nowv];
            //下面将把 f[nowv] 插入到 set 中
            //找到标号小于 nowv 且代价大于 nowv 的状态
            auto it=st.lower_bound(make_pair(nowv,0));
            // 不断删除
            while ((it!=st.begin() && (it--,1)) && (it->second > tf))
                st.erase(it),it=st.lower_bound(make_pair(nowv,0));
            //插入
            st.insert(make_pair(nowv,tf));
        }
        tpre=max(tpre,nowv);
        //以下是贪心
        if (nowv>tx)
            tx=nowv,ans1++;
        for (int ei=fst[nowv];ei;ei=nxt[ei]){
            int v=g[ei];
            pre[v]=max(pre[v],tpre);
            ind[v]--;
            (!ind[v])?(q.push(v)):(void());
        }
    }
    ayano(ans1);
    ayano(f[n]);
    return 0;
}
