int ans1, ans2;
int main()
{
	scanf("%d%d", &n, &m);
	for (int i = 0; i < m; ++i)
	{
		int x, y;
		scanf("%d%d", &x, &y);
		lian(x, y);
		++ ru[y];
		++ ru1[y];
	}
	for (int i = 1; i <= n; ++i)
	{
		if (ru[i] == 0)
		{
			q1.push(i);
			q2.push(i);
		}
	}
	int maxs = 0;
	while(!q1.empty())
	{
		int now = q1.top();
