```cpp
#include<cstdio>
#include<queue>
#include<vector>
using namespace std;
/*  It is day of judgement!  */
int n, m, tot, x, y, ans, maxx, u, now;
const int N = 500010;
int head[N], in1[N], in2[N];
struct bian {int to, nt;} e[N << 1];
priority_queue<int, vector<int>, greater<int> >Q;
priority_queue<int>q[2];
void add(int f, int t) 
{
	e[++tot].to = t;
	e[tot].nt = head[f];
	head[f] = tot;
}
int main() 
{
	scanf("%d%d", &n, &m);
	for (int i = 1; i <= m; ++i) 
	{
		scanf("%d%d", &x, &y);
		add(x, y);
		in1[y]++; in2[y]++;
	}
	for (int i = 1; i <= n; ++i)
		if (!in1[i])Q.push(i);
	while (!Q.empty())
	{
		u = Q.top(); Q.pop();
		if (u > maxx) {++ans; maxx = u;}
		for (int i = head[u]; i; i = e[i].nt)
			if (!(--in1[e[i].to]))Q.push(e[i].to);
	}
	printf("%d\n", ans);
	/*上面为问题1，下面是问题2*/
	ans = 0; maxx = 0;
	for (int i = 1; i <= n; ++i)
		if (!in2[i])q[0].push(i);
	while ((!q[0].empty()) || (!q[1].empty())) 
	{
		u = q[now].top();
		if (u > maxx) {++ans; maxx = u;}
		while (!q[now].empty()) 
		{
			u = q[now].top(); q[now].pop();
			for (int i = head[u]; i; i = e[i].nt)
				if (!(--in2[e[i].to])) 
				{
					if (e[i].to < maxx)q[now].push(e[i].to);
					else q[now ^ 1].push(e[i].to);
				}
		}
		now ^= 1;
	}
	return printf("%d", ans) == 2333;
}
