	}
	while(isdigit(c)){
		w=w*10+c-'0';
		c=getchar();
	}
	return s?-w:w;
}
const int N=500005,inf=2147483647;
int n,m;
struct Treap{
	int cnt,root;
	int son[N][2],val[N],wei[N],size[N],cpy[N];
	il int add(int v){
		val[++cnt]=v;
		size[cnt]=1;
		cpy[cnt]=1;
		wei[cnt]=rand();
		return cnt;
	}
	il void pushup(int id){
		size[id]=size[son[id][0]]+size[son[id][1]]+cpy[id];
	}
	il void init(){
		memset(son,0,sizeof(son));
		memset(val,0,sizeof(val));
		memset(wei,0,sizeof(wei));
		memset(size,0,sizeof(size));
		memset(cpy,0,sizeof(cpy));
		cnt=0;
		root=0;
		root=add(-inf);
		son[root][1]=add(inf);
		pushup(root);
	}
	il void rotate(int &id,int d){
		int e=son[id][d^1];
		son[id][d^1]=son[e][d];
		son[e][d]=id;
		id=e;
		pushup(son[id][d]);
		pushup(id);
	}	
	void insert(int &id,int v){
		if(!id){
			id=add(v);
			return;
		}
		if(v==val[id]){
			cpy[id]++;
		}	
		else{
			int d=(v<val[id]?0:1);
			insert(son[id][d],v);
			if(wei[id]<wei[son[id][d]]){
				rotate(id,d^1);
			}
		}
		pushup(id);
	}	
	void remove(int &id,int v){
		if(!id){
			return;
		}
		if(v==val[id]){
			if(cpy[id]>1){
				cpy[id]--;
				pushup(id);
				return;
			}	
			else if(son[id][0]||son[id][1]){
				if(!son[id][1]||wei[son[id][0]]>wei[son[id][1]]){
					rotate(id,1);
					remove(son[id][1],v);
				}
				else{
					rotate(id,0);
					remove(son[id][0],v);
				}
				pushup(id);	
			}
			else{
				id=0;
			}	
			return;	
		}	
		else if(v<val[id]){
			remove(son[id][0],v);
		}
		else{
			remove(son[id][1],v);
		}	
		pushup(id);
	}	
	int rank(int id,int v){
		if(!id){
			return 0;
		}
		if(v==val[id]){
			return size[son[id][0]]+1;
		}	
		else if(v<val[id]){
			return rank(son[id][0],v);
		}	
		else{
			return size[son[id][0]]+cpy[id]+rank(son[id][1],v);
		}	
	}
	int value(int id,int v){
		if(!id){
			return inf;
		}	
		if(v<=size[son[id][0]]){
			return value(son[id][0],v);
		}	
		else if(v<=size[son[id][0]]+cpy[id]){
			return val[id];
		}	
		else{
			return value(son[id][1],v-size[son[id][0]]-cpy[id]);
		}	
	}
	il int pre(int v){
		int id=root;
		int res;
		while(id){
			if(val[id]<v){
				res=val[id];
				id=son[id][1];
			}
			else{
				id=son[id][0];
			}	
		}
		return res;	
	}
	il int nxt(int v){
		int id=root;
		int res;
		while(id){
			if(val[id]>v){
				res=val[id];
				id=son[id][0];
			}
			else{
				id=son[id][1];
			}	
		}
		return res;
	}
	il void Push(int x){
		insert(root,x);
	}
	il void Pop(int x){
		remove(root,x);
	}
	il int Find(int x){
		int res=rank(root,x);
		return res-1;
	}
	il int Pos(int x){
		int res=value(root,x+1);
		return res;
	}
	il int Lower(int x){
		int res=pre(x);
		return res;
	}
	il int Upper(int x){
		int res=nxt(x);
		return res;
	}
	il bool Empty(){
		return Lower(inf)==-inf;
	}
};
Treap T;
struct Graph{
	struct Edge{
		int to;
		Edge *nxt;
	};
	Edge e[N],*pre[N];
	int cnt;
	il void add_for(int u,int v){
		e[++cnt].nxt=pre[u];
		e[cnt].to=v;
		pre[u]=&e[cnt];
	}
	Graph(){
		memset(pre,0,sizeof(pre));
		cnt=0;
	}
};
Graph G;
int deg[N],cpy[N];
int tmp,ans;
int main(){
	srand((int)time(0));
	n=read(),m=read();
	T.init();
	int u,v;
	for(rg int i=1;i<=m;i++){
		u=read(),v=read();
		G.add_for(u,v);
		deg[v]++;
		cpy[v]++;
	}
	for(rg int i=1;i<=n;i++){
		if(!deg[i]){
			T.Push(i);
		}
	}
	while(!T.Empty()){
		u=T.Pos(1);
		T.Pop(u);
		if(u>tmp){
			tmp=u;
			ans++;
		}
		for(Graph::Edge *i=G.pre[u];i!=NULL;i=i->nxt){
			v=i->to;
			if(!--deg[v]){
				T.Push(v);
			}
		}
	}
	printf("%d\n",ans);
	T.init();
	for(rg int i=1;i<=n;i++){
		deg[i]=cpy[i];
		if(!deg[i]){
			T.Push(i);
		}
	}
	tmp=0,ans=0;
	while(!T.Empty()){
		while(T.Upper(-inf)<tmp&&!T.Empty()){
			u=T.Upper(-inf);
			T.Pop(u);
			for(Graph::Edge *i=G.pre[u];i!=NULL;i=i->nxt){
				v=i->to;
				if(!--deg[v]){
					T.Push(v);
				}
			}
		}
		if(T.Empty()){
			break;
		}
		ans++;
		tmp=T.Lower(inf);
		u=tmp;
		T.Pop(u);
		for(Graph::Edge *i=G.pre[u];i!=NULL;i=i->nxt){
			v=i->to;
			if(!--deg[v]){
				T.Push(v);
			}
		}
	}
	printf("%d",ans);
	return 0;
}
