```cpp
#include <cstdio>
#include <algorithm>
using namespace std;

int n, m, k, a[17];
int main() {
	scanf("%d%d%d", &n, &m, &k);
	for(int i = 1; i <= k; ++i)
		scanf("%d", &a[i]);
	int cur = 0, ans = 0;
	while(cur < n) {
		cur += m;
		ans++;
		for(int i = 1; i <= k; ++i)
			if(cur == a[i])
				m++;
	}
	printf("%d", ans);
	return 0;
}
```

