#include <cstdio>
#include <algorithm>
using namespace std;

const int N = 5003, MOD = 1000000007;
int Q, n;

int a[N], f[N], ans;
int g[N * N >> 1], num[N * N >> 1];

inline int ksm (int p, int x) {
	int s = 1;
	while (x) {
		if (x & 1) s = 1ll * s * p % MOD;
		p = 1ll * p * p % MOD;
		x >>= 1;
	}
	return s;
}

int main () {
	int i, now = 0; n = 5000;
	g[0] = 0;
	num[0] = 1;
	for (i = 1; i <= n; ++i) {
		f[i] = f[i - 1];
		for (register int j = i + 1; j <= now; ++j) {
			f[i] += 1ll * (g[j] + num[j]) % MOD;
			f[i] = f[i] >= MOD ? f[i] - MOD : f[i];
		}
		for (register int j = now; ~j; --j) {
			g[j + i] += g[j] + num[j];
			num[j + i] += num[j];
			num[j + i] %= MOD;
			g[j + i] %= MOD;
		}
		now += i;
	} //n^3方暴力转移

	scanf ("%d", &Q);
	while (Q--) {
		scanf ("%d", &n);
		printf ("%d\n", 1ll * f[n] * ksm (ksm (2, n), MOD - 2) % MOD);
	} //只要令输入为1~5000就可以把所有答案跑出来啦。
	return 0;
}
