	int sum=build(1,0);sum=0;give_value(1,0,n);
	for (int i=1;i<2*n;i+=2) sum+=val[i];
	for (int i=1;i<=n;++i) for (int j=head[i];j;j=nxt[j]) ppp[i]+=val[j];
