using namespace std;
typedef long long ll;
const int N=500005;

inline ll read(){
    ll x=0,f=1;char ch=getchar();
    while(ch<'0'||ch>'9'){if(ch=='-')f=-1;ch=getchar();}
    while(ch>='0'&&ch<='9'){x=(x<<1)+(x<<3)+(ch^48);ch=getchar();}
    return x*f;
}

ll n,m,d,q,a[N],b[65],lg[N],st[N][30],l,r,tmp;
