	for(int i = 1; i <= m; i++) {
		scanf("%lld", &b1[i]);
	}
	sort(b1 + 1, b1 + m + 1);
	for(int i = 1; i <= m; i++) {
		b[b1[i]] = i;
	}
	long long tmp;
	for(int i = 1; i <= n; i++) {
		tmp = a[i];
		a[i] = 114514; 
		while(true) {
			if(b.find(tmp) != b.end()) {
				a[i] = b[tmp];
			}
			if(tmp == 0) {
				break;
			}
			tmp /= x;
		}
	}
	for(int i = 1; i <= n; i++) {
		for(int j = 1; j <= m; j++) {
			if(a[i] == j) {
				dp[i][j] = dp[i - 1][j] + 1;
			} else {
				dp[i][j] = dp[i - 1][j];
			}
		}
	}
	int l, r;
	int ans = 0;
	for(int i = 1; i <= q; i++) {
		scanf("%d%d", &l, &r);
		ans = 0;
		for(int i = 1; i <= m; i++) {
			if(dp[r][i] > dp[l - 1][i]) {
