	q=read();
	x=read()-1;
	for(int i=1;i<n;i++){
		int u,v;
		u=read()-1;
		v=read()-1;
		t[u].push_back(v);
		t[v].push_back(u);
	}
	for(int i=1;i<(1<<n);i++) sz[i]=sz[i>>1]+(i&1);
	for(int i=1;i<(1<<n);i++){
		dfs(x,-1,i);
		ans[i]=f[x][1]*(sz[i]%2==0?mod-1:1)%mod;
	}
	FWT(ans,1<<n,1);
	while(q--){
		int k=read(),s=0;
		while(k--) s|=1<<(read()-1);
