	cin>>s;
	l=strlen(s);
	for(int i=1;i<=l;i++){
		a[i]=s[i-1]-48;//字符转数字
	}
	for(int i=1;i<=l;i++){
		for(int j=i;j<=l;j++){
			if(a[j]==0) o++; //计算0出现的个数
			if(a[j]==1) p++; //计算1出现的个数
