			d[i]=edge[i];
//			cout<<edge[i]<<" ";
		}
		goto lyk;
	}
	dijkstra(1);
	lyk:;
//	for(int i=1;i<=n;i++) cout<<d[i]<<" ";
	for(int i=1;i<=q;i++)
	{
		ll x=read(),y=read();
//		cout<<d[x]<<" "<<d[y]<<endl;
